pragma solidity ^0.4.23;

import "./Store.sol";

contract StoreOwner {

  //============================================================================
  // VARIABLES

  mapping(address => bool) public stores;


  //============================================================================
  // EVENTS

  event AddedStore(address indexed _storeOwnerAddress, address _storeAddress);
  event RemovedStore(address indexed _storeOwnerAddress, address _storeAddress);


  //============================================================================
  // FUNCTIONS

  /**
   * TODO
   */
  function addStore()
  public
  returns(address)
  {
    Store store = new Store();
    stores[store] = true;
    emit AddedStore(msg.sender, store);
    return store;
  }

  /**
   * TODO
   */
  function removeStore(address _storeAddress)
  public
  returns(bool)
  {
    stores[_storeAddress] = false;
    emit RemovedStore(msg.sender, _storeAddress);
    return true;
  }
}
